import BearCol from "./BearCol";
import BullCol from "./BullCol";
import TechnicalIndicators from "./TechnicalIndicators";

import { useState, useEffect } from "react";

const MainContainer = (props) => {
  //STATES ----------------------------------------------
  const [downerStocks, setDowner] = useState([
    {
      id: 1,
      active: false,
    },
    {
      id: 2,
      active: false,
    },
    {
      id: 3,
      active: false,
    },
    {
      id: 4,
      active: false,
    },
    {
      id: 5,
      active: false,
    },
  ]);
  const [upperStocks, setUpper] = useState([
    {
      id: 6,
      active: false,
    },
    {
      id: 7,
      active: false,
    },
    {
      id: 8,
      active: false,
    },
    {
      id: 9,
      active: false,
    },
    {
      id: 10,
      active: false,
    },
  ]);

  const [finalUpperArr, setFinalUpperArr] = useState([])
  const [finalDownerArr, setFinalDownerArr] = useState([])
  //ID CHECK TO CLOSE BOX ---------------------------------------------------------
  const [currentActiveBox, setCurrentActiveBox] = useState(0);
  const [openApp, setOpenApp] = useState(null);

  // EFFECTS -==============----==============================================================
  useEffect(() => {

    for (let i = 0; i < props.pulledData.length; i++) {
      props.pulledData[i].id = i;
      props.pulledData[i].active = false;
    }

    setFinalDownerArr(props.pulledData.slice(0,5))
    setFinalUpperArr(props.pulledData.slice(5,))

  }, [props.pulledData])

  useEffect(() => {
    if (openApp == false) {
      setTimeout(() => {
        setOpenApp(true);
      }, 500);
    }
  }, [openApp]);

// FUNCTIONS ---------------------------------------------------------------------------
  const toggleActiveUpper = (id) => {
    console.log(id)
    if (currentActiveBox == id) {
      setOpenApp(null);
      for (const upper of finalUpperArr) {
        upper.active = false;
      }
      setCurrentActiveBox(0)
      return;
    }

    for (const upper of finalUpperArr) {
      if (id == upper.id) {
        upper.active = true;
      } else {
        upper.active = false;
      }
    }

    for (const downer of finalDownerArr) {
      downer.active = false;
    }

    setUpper([...upperStocks]);
    setDowner([...downerStocks]);

    setOpenApp(!openApp);

    setCurrentActiveBox(id);
  };
  const toggleActiveDowner = (id) => {
    console.log(id)
    if (currentActiveBox == id) {
      setOpenApp(null);
      for (const downer of finalDownerArr) {
        downer.active = false;
      }
      setCurrentActiveBox(0)
      return;
    }

    for (const downer of finalDownerArr) {
      if (id == downer.id) {
        downer.active = true;
      } else {
        downer.active = false;
      }
    }

    for (const upper of finalUpperArr) {
      upper.active = false;
    }

    setUpper([...upperStocks]);
    setDowner([...downerStocks]);

    setOpenApp(!openApp);

    setCurrentActiveBox(id);
  };
  // RENDER ========================================================

  return (
    <main>
      <BearCol
        toggleActiveDowner={toggleActiveDowner}
        downerStocks={downerStocks}
        openApp={openApp}
        finalDownerArr={finalDownerArr}
      />

<TechnicalIndicators pulledData={props.pulledData} currentActiveBox={currentActiveBox}/>

      <BullCol
        toggleActiveUpper={toggleActiveUpper}
        upperStocks={upperStocks}
        openApp={openApp}
        finalUpperArr={finalUpperArr}
      />

    </main>
  );
};

export default MainContainer;
