import {useState, useEffect} from 'react'

const PortfolioSettings = (props) => {

    const [checked, setChecked] = useState(true)

   const handleSubmit = (e) => {
    e.preventDefault()
    // console.log(checked) // CHECK WHICH ONE IS MARKED
    props.handleSaveSettings()
    props.changeDefaultOrPortfolio(checked)
   }

   const handleChangeSettings = (e) => {
       setChecked(e.target.value)
       if (checked === 'default') {
        setChecked(true)
    } else if (checked === 'portfolio') {
        setChecked(false)
    }
   }



   useEffect(() => {
    const checkedStorage = JSON.parse(localStorage.getItem('deadCatSettings'));

    console.log(checkedStorage, 'in settings')
    if (checkedStorage === 'default') {
        setChecked(true)
    } else {
        setChecked(false)
    }

   }, [])

// NEED TO CHANGE LATER ---------- HAVE THE CHECKED BTN BE WHATEVER IS IN LOCAL STORAGE
    return(
        <div className={props.showSettings ? "active-settings portfolio-settings-cont" : "portfolio-settings-cont" }>
            <form onSubmit={(e) => handleSubmit(e)}>
                    <fieldset>
                        <legend>What To Show On Load</legend>
                    <label checked={checked ? true : false} htmlFor="default-setting">Default</label>
                    <input tabIndex={props.activePortfolio ? -1 : 0} onChange={(e) => handleChangeSettings(e)} name="settings" id="default-setting" type="radio" value="default"/>
                    <label htmlFor="portfolio-setting">Show Portfolio</label>
                    <input checked={checked ? false : true} tabIndex={props.activePortfolio ? -1 : 0} onChange={(e) => handleChangeSettings(e)} name="settings" id="portfolio-setting" type="radio" value="portfolio"/>
                    <input type="submit" value="Save Settings"/>
                    </fieldset>
            </form>
        </div>
    )
}

export default PortfolioSettings;
