import {useState, useEffect} from 'react'
const PortfolioSettings = (props) => {
    const [checked, setChecked] = useState(true)
    const [inputVal, setInputVal] = useState('default')
    // localStorage.clear()
   const handleSubmit = (e) => {
    e.preventDefault()
    props.handleSaveSettings()
    props.changeDefaultOrPortfolio(inputVal)
   }

   const handleChangeSettings = (e) => {
       console.log(e.target.value, 'change')
       setInputVal(e.target.value)
   }

   useEffect(() => {
    console.log(inputVal, 'inputval effect')
     if (inputVal === null) {
        setChecked(true)
      } else if (inputVal === 'default') {
        setChecked(true)
    } else if (inputVal === 'portfolio') {
        setChecked(false)
    }
   }, [inputVal])
   useEffect(() => {
    console.log('render')
   }, [checked])



   useEffect(() => {
    const checkedStorage = JSON.parse(localStorage.getItem('deadCatSettings'));

    console.log(inputVal, 'load')
    if (checkedStorage === null) {
        setInputVal('default')
        setChecked(true)
      } else if (checkedStorage === 'default') {
        setChecked(true)
    } else if (checked === 'portfolio') {
        setChecked(false)
    }
   }, [])

// NEED TO CHANGE LATER ---------- HAVE THE CHECKED BTN BE WHATEVER IS IN LOCAL STORAGE
    return(
        <div className={props.showSettings ? "active-settings portfolio-settings-cont" : "portfolio-settings-cont" }>
            <form onSubmit={(e) => handleSubmit(e)}>
                    <fieldset>
                        <legend>What To Show On Load</legend>
                    <label  htmlFor="default-setting">Default</label>
                    {checked ? <input defaultChecked tabIndex={props.activePortfolio ? -1 : 0} onChange={(e) => handleChangeSettings(e)} name="settings" id="default-setting" type="radio" value="default"/> : <input tabIndex={props.activePortfolio ? -1 : 0} onChange={(e) => handleChangeSettings(e)} name="settings" id="default-setting" type="radio" value="default"/>}
                    <label htmlFor="portfolio-setting">Show Portfolio</label>
                    {checked ? <input tabIndex={props.activePortfolio ? -1 : 0} onChange={(e) => handleChangeSettings(e)} name="settings" id="portfolio-setting" type="radio" value="portfolio"/> : <input defaultChecked tabIndex={props.activePortfolio ? -1 : 0} onChange={(e) => handleChangeSettings(e)} name="settings" id="portfolio-setting" type="radio" value="portfolio"/>}
                    <input type="submit" value="Save Settings"/>
                    </fieldset>
            </form>
        </div>
    )
}

export default PortfolioSettings;
