import PortfolioItem from "./PortfolioItem";
import { useState, useEffect } from "react";


const PortfolioHolder = (props) => {

    const [storageArr, setStorageArr] = useState([])

    useEffect(() => {

        setStorageArr(localStorage.getItem(JSON.parse("deadCatPortfolio")))
    }, [props.newStorage])

    return(
        <div className="portfolio-holder">
            <hr></hr>

            <PortfolioItem />


            <button onClick={props.handleClearPortfolio} tabIndex={props.activePortfolio ? -1 : 0} className="clear-portfolio">Clear Entire Portfolio</button>
        </div>
    )   
}

export default PortfolioHolder;
