import './App.css';
import ErrorBoundary from './ErrorPage';
import Header from './components/Header';
import MainContainer from './components/MainContainer'
import {useEffect, useState} from 'react';

import { technicalAnalysis } from './JS/ApiFunction';
import { searchedSymbolTA } from './JS/handleSearchSymbol';


function App() {
  const [pulledData, setPulledData] = useState([])
  const [searchedSymbol, setSearchedSymbol] = useState('')
  const [searchActive, setSearchActive] = useState(false)
  const [searchedPulledData, setSearchedPulledData] = useState([])


useEffect(() => {
  technicalAnalysis().then(res => setPulledData(res))
}, [])

const handleSearch = (symbol) => {
   setSearchedSymbol(symbol)
  setSearchActive(true)
  setTimeout(() => {
    setSearchActive(false)
  }, 1000);
}

useEffect(() => {
  if (searchActive) {
    searchedSymbolTA(searchedSymbol).then(res => setSearchedPulledData(res))
  } else {
    return;
  }
}, [searchActive])

  return (
    <ErrorBoundary>
      <div className="App">
        <Header handleSearch={handleSearch}/>
        <MainContainer searchedSymbol={searchedSymbol} searchedPulledData={searchedPulledData} pulledData={pulledData}/>
      </div>
    </ErrorBoundary>
  );
}

export default App;
