import BearRow from "./BearRow";
import BullRow from "./BullRow";

import { useState, useEffect } from "react";

const MainContainer = () => {
  //STATES ----------------------------------------------
  const [downerStocks, setDowner] = useState([
    {
      name: "ABCD",
      price: 2.34,
      percentage: "25",
      change: 0.53,
      id: 1,
      active: false,
    },
    {
      name: "ABCD",
      price: 100.34,
      percentage: "9",
      change: 0.453,
      id: 2,
      active: false,
    },
    {
      name: "ABCD",
      price: 1.34,
      percentage: "1",
      change: 1.153,
      id: 3,
      active: false,
    },
    {
      name: "ABCD",
      price: 6.34,
      percentage: "51",
      change: 0.03,
      id: 4,
      active: false,
    },
    {
      name: "ABCD",
      price: 4.34,
      percentage: "5",
      change: 0.33,
      id: 5,
      active: false,
    },
  ]);
  const [upperStocks, setUpper] = useState([
    {
      name: "ABCD",
      price: 2.34,
      percentage: "25",
      change: 0.53,
      id: 1,
      active: false,
    },
    {
      name: "ABCD",
      price: 100.34,
      percentage: "9",
      change: 0.453,
      id: 2,
      active: false,
    },
    {
      name: "ABCD",
      price: 1.34,
      percentage: "1",
      change: 1.153,
      id: 3,
      active: false,
    },
    {
      name: "ABCD",
      price: 6.34,
      percentage: "51",
      change: 0.03,
      id: 4,
      active: false,
    },
    {
      name: "ABCD",
      price: 4.34,
      percentage: "5",
      change: 0.33,
      id: 5,
      active: false,
    },
  ]);
  const [openApp, setOpenApp] = useState(false)


useEffect(() => {
  if (openApp == false) {
    setTimeout(() => {
      setOpenApp(true)
    }, 500)
  }
}, [openApp])

  const toggleActiveUpper = (id) => {
    for (const upper of upperStocks) {
      if (id == upper.id) {
        upper.active = true;
      } else {
        upper.active = false;
      }
    }

    for (const downer of downerStocks) {
      downer.active = false;
    }

    setUpper([...upperStocks]);
    setDowner([...downerStocks]);

    setOpenApp(!openApp)
  };
  const toggleActiveDowner = (id) => {
    for (const downer of downerStocks) {
      if (id == downer.id) {
        downer.active = true;
      } else {
        downer.active = false;
      }
    }

    for (const upper of upperStocks) {
      upper.active = false;
    }

    setUpper([...upperStocks]);
    setDowner([...downerStocks]);

    setOpenApp(!openApp)
  };

  return (
    <main>
      <BearRow
        toggleActiveDowner={toggleActiveDowner}
        downerStocks={downerStocks}
        openApp={openApp}
      />
      <BullRow
        toggleActiveUpper={toggleActiveUpper}
        upperStocks={upperStocks}
        openApp={openApp}
      />
    </main>
  );
};

export default MainContainer;
