    } else if (obj.marketDay == 6) {
      obj.date = obj.date - 1;
      // NEED TO CHECK FOR NEW obj.MONTH CHANGE OVER FROM UTC TIME TO ADJUST FOR EST
      if (obj.date <= 0) {
