          // obj.MONTHS AFTER 30 DAYS
          obj.month = obj.month - 1;
          if (obj.month < 10) {
            obj.month = `0${obj.month}`;
          }
          obj.date = 30;
        } else if (obj.month == 3 && obj.year % 4 == 0) {
          // obj.MONTH AFTER 29 DAYS LEAP obj.YEAR
          obj.month = obj.month - 1;
          if (obj.month < 10) {
            obj.month = `0${obj.month}`;
          }
          obj.date = 29;
        } // obj.MONTH AFTER 28
        else {
          obj.month = obj.month - 1;
          if (obj.month < 10) {
            obj.month = `0${obj.month}`;
          }
          obj.date = 28;
        }
