}
  setactivePortfolio(false)
  setActivePortfolioZ(false)
}
const handleSearch = (symbol) => {
    setactivePortfolio(!activePortfolio)
    setSearchedSymbol(symbol)
    setSearchActive(true)
    setTimeout(() => {
      setSearchActive(false)
    }, 1000);
}
// SEARCH API ---------------
useEffect(() => {
  if (searchActive) {
    searchedSymbolTA(searchedSymbol).then(res => setSearchedPulledData(res))
  } else {
    return;
  }
}, [searchActive])
// END SEARCH API ---------------

  return (
      <div className="App">
          <Header handleOpenPortfolio={handleOpenPortfolio} handleSearch={handleSearch}/>
          <MainContainer changeDefaultOrPortfolio={changeDefaultOrPortfolio} handleOpenPortfolio={handleOpenPortfolio} activePortfolioZ={activePortfolioZ} activePortfolio={activePortfolio} searchedSymbol={searchedSymbol} searchedPulledData={searchedPulledData} pulledData={pulledData}/>
      </div>

  );
}

export default App;
