import {useState, useEffect} from 'react'
const PortfolioSettings = (props) => {
localStorage.clear()
    const [checked, setChecked] = useState(null)
    const [inputVal, setInputVal] = useState('')

   const handleSubmit = (e) => {
    e.preventDefault()
    // console.log(checked) // CHECK WHICH ONE IS MARKED
    props.handleSaveSettings()
    console.log(inputVal, 'submited val')
    props.changeDefaultOrPortfolio(inputVal)
    localStorage.setItem('deadCatSettings', JSON.stringify(inputVal))
   }

   const handleChangeSettings = (e) => {
       setInputVal(e.target.value)
       console.log(inputVal, 'input val change')
   }

//    useEffect(() => {
//     console.log(checked)
//     if (checked === 'default') {
//         setChecked(true)
//     } else if (checked === 'portfolio') {
//         setChecked(false)
//     }
//    }, [checked])


   useEffect(() => {
    const checkedStorage = JSON.parse(localStorage.getItem('deadCatSettings'));

    if (checkedStorage === 'default') {
        setChecked(true)
    } else if (checked === 'portfolio') {
        setChecked(false)
    }
    console.log(checkedStorage, 'in settings', checked)

   }, [])

// NEED TO CHANGE LATER ---------- HAVE THE CHECKED BTN BE WHATEVER IS IN LOCAL STORAGE
    return(
        <div className={props.showSettings ? "active-settings portfolio-settings-cont" : "portfolio-settings-cont" }>
            <form onSubmit={(e) => handleSubmit(e)}>
                    <fieldset>
                        <legend>What To Show On Load</legend>
                    <label  htmlFor="default-setting">Default</label>
                    {checked ? <input  tabIndex={props.activePortfolio ? -1 : 0} onChange={(e) => handleChangeSettings(e)} name="settings" id="default-setting" type="radio" value="default"/> : <input defaultChecked  tabIndex={props.activePortfolio ? -1 : 0} onChange={(e) => handleChangeSettings(e)} name="settings" id="default-setting" type="radio" value="default"/>}
                    <label htmlFor="portfolio-setting">Show Portfolio</label>
                    {checked ? <input defaultChecked tabIndex={props.activePortfolio ? -1 : 0} onChange={(e) => handleChangeSettings(e)} name="settings" id="portfolio-setting" type="radio" value="portfolio"/> : <input  tabIndex={props.activePortfolio ? -1 : 0} onChange={(e) => handleChangeSettings(e)} name="settings" id="portfolio-setting" type="radio" value="portfolio"/>}
                    <input defaultChecked type="submit" value="Save Settings"/>
                    </fieldset>
            </form>
        </div>
    )
}

export default PortfolioSettings;
