import PortfolioItem from "./PortfolioItem";
import { useState, useEffect } from "react";

const PortfolioHolder = (props) => {
  const [storageArr, setStorageArr] = useState(null);
  const [isEmpty, setIsEmpty] = useState(true);
// THIS IS FOR DELETING

  const handleDeleteStorageItem = (id) => {
    setStorageArr(storageArr.filter((item) => item.id !== id));
  };

  useEffect(() => {
    if (props.newStorage === false) return; // WILL STOP FROM RUNNING
    setStorageArr(JSON.parse(localStorage.getItem("deadCatPortfolio")));
    setIsEmpty(false)
  }, [props.newStorage]);


  useEffect(() => {
    console.log(storageArr, 'storage Effect')

    if (storageArr === null) {
      return
    }

    console.log(storageArr, 'storage Effect')
    // if (storageArr === []) {
    //   setIsEmpty(true)
    // }

    // if (storageArr === []) {
    //   localStorage.clear("deadCatPortfolio")
    //   return
    // }

    localStorage.setItem("deadCatPortfolio", JSON.stringify(storageArr))
    setIsEmpty(false)
  }, [storageArr])

  // useEffect(() => {
  //   console.log(storageCheck)

  //   const storageCheck = JSON.parse(localStorage.getItem("deadCatPortfolio"));
  //   if (storageCheck === null) {
  //     setIsEmpty(true);
  //     console.log(storageCheck)
  //   } else {
  //     setIsEmpty(true)
  //   }
  // }, [])

if (isEmpty) {
  return (
    <div className="portfolio-holder">
      <hr></hr>

      <p>Empty</p>

      <button
        onClick={props.handleClearPortfolio}
        tabIndex={props.activePortfolio ? -1 : 0}
        className="clear-portfolio"
      >
        Clear Entire Portfolio
      </button>
    </div>
  );
  } else {
    return (
      <div className="portfolio-holder">
        <hr></hr>

        {storageArr.map((data) => (
          <PortfolioItem
            handleDeleteStorageItem={handleDeleteStorageItem}
            key={data.id}
            data={data}
          />
        ))}

        <button
          onClick={props.handleClearPortfolio}
          tabIndex={props.activePortfolio ? -1 : 0}
          className="clear-portfolio"
        >
          Clear Entire Portfolio
        </button>
      </div>
    );
  }
};

export default PortfolioHolder;
